import React, { useState } from 'react';
import { BrowserRouter as Link } from 'react-router-dom';
import axios from "axios";

const Footer = () => {
    const [email, setEmail] = useState("");
    const [confirmMsg, setConfirmMsg] = useState("  ");

    const submitEmailList = (e) => {
        try {
            axios.post("/api/submitEmailList", {
                params: {
                    email: email
                }
            }).then(response => {
                console.log(response.data);
                if (response.data === true) {
                    setConfirmMsg("You're in!");
                } else if (response.data.code === "ER_DUP_ENTRY") {
                    setConfirmMsg("Email address has already subscribed.");
                } else {
                    setConfirmMsg("Something went wrong.");
                }
            })
        } catch (err) {
            console.log(err);
            setConfirmMsg("An error occured.");
        }
        e.preventDefault();
    }

    return (
        <footer>
          <div className="footer-outer">
            <div className="footer-inner">
              <div className="footer-email-outer">
                <div className="footer-email-inner">
                  <h3>Like the product? Help share the word and get more restaurants to get rid of physical menus.</h3>
                </div>
              </div>
              <div className="footer-up">
                <div className="footer-social">
                  <ul>
                    <a className="profile-social-icon" rel="external noopener noreferrer" href="https://twitter.com/fsundgren" target="_blank">
                      <i className="fa fa-twitter"></i>
                    </a>
                    <a className="profile-social-icon footer-envelope-icon" href="mailto:filip@vippalo.com">
                      <i className="fa fas fa-envelope"></i>
                    </a>
                  </ul>
                </div>
              </div>
            </div>
            <div className="bottom-container">
              <div className="footer-down">
                <p>Copyright Â© 2020 Vippalo</p>
              </div>
            </div>

          </div>
          <div className="stripes" />
        </footer>
    );
}
export default Footer;
