import React, { useState, useEffect } from 'react';
//import Web3 from "web3";
import MyStringStore from "./contracts/MyStringStore.json";
import getWeb3 from "./web3/getWeb3";

import "./App.css";

function App() {
    const [appState, setAppState] = useState({
        contractString: "",
        web3: {},
        accounts: {},
        contract: {}
    });

  // state = { storageValue: 0, web3: null, accounts: null, contract: null };
    useEffect(() => {
        initSetup();
        runExample();
    }, []);


    const initSetup = async () => {
        try {
          // Get network provider and web3 instance.
          const web3 = await getWeb3();
          // console.log("get web3: ", web3);

          // Use web3 to get the user's accounts.
          const accounts = await web3.eth.getAccounts();

          // Get the contract instance.
          const networkId = await web3.eth.net.getId();
          const deployedNetwork = await MyStringStore.networks[networkId];
          const instance = await new web3.eth.Contract(
            MyStringStore.abi,
            deployedNetwork && deployedNetwork.address,
          );

          // Set web3, accounts, and contract to the state, and then proceed with an
          // example of interacting with the contract's methods.
          // setAppState({
          //     storageValue: 0,
          //     web3: web3,
          //     accounts: accounts,
          //     contract: instance
          // });
          setAppState(prevState => {
              return { ...prevState, web3: web3 }
          });
          setAppState(prevState => {
              return { ...prevState, accounts: accounts }
          });
          setAppState(prevState => {
              return { ...prevState, contract: instance }
          });
          // console.log("accounts::", accountsInit);
          // setWeb3(web3Init);
          // setAccounts(accountsInit);
          // setContract(instance);
          // console.log(web3, accounts, contract);
          // await setAppState(prevState => ({
          //     ...prevState,
          //     web3: web3,
          //     accounts: accounts,
          //     contract: instance
          // }));
          // console.log("appState: ", appState);
        } catch (err) {
            // Catch any errors for any of the above operations.
            console.log("error man: ", err);
        }
    }


    const runExample = async () => {
        console.log("inside runExample");
        // const { accounts, contract } = appState;

        // Stores a given value, 5 by default.
        try {
            await appState.contract.methods.set("hejhej från kontraktet").send({ from: appState.accounts[0] });

            // Get the value from the contract to prove it worked.
            const response = await appState.contract.methods.get().call();

            // Update state with the result.
            // setAppState({
            //     storageValue: response,
            //     web3: appState.web3,
            //     accounts: appState.accounts,
            //     contract: appState.contract
            // });
            setAppState(prevState => {
                return { ...prevState, contractString: response }
            });
            console.log("APPP:: ", appState);
            // setStorageValue(response);
        } catch (err) {
            console.log("error in runExample: ", err);
        }
    };

    return(
      <>
      {!appState.web3 ? <div>Loading Web3, accounts, and contract...</div>
        :
        <div className="App">
          <h1>Good to Go!</h1>
          <p>Your Truffle Box is installed and ready.</p>
          <h2>Smart Contract Example</h2>
          <p>
            Try changing the value stored on <strong>line 40</strong> of App.js.
          </p>
          <div>Contract string is: {appState.contractString}</div>
        </div>
        }
      </>
    )
}

export default App;
