import React, { useState } from 'react';
import { useLocation, useHistory } from 'react-router-dom';


const Register = (props) => {
    let location = useLocation();
    const history = useHistory();
    const [userData, setUserData] = useState({
        id: "",
        full_name: "",
        profile_image: "",
        pres_text: "",
        address: "",
        charity: "",
        price: "",
    });
    const [profile, setProfile] = useState(null);


    const registerUser = async (e) => {
        e.preventDefault();

        cleanUserData();
        window.scrollTo(0, 0);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevValue => {
            return {
                ...prevValue,
                [name]: value
            };
        });
        e.preventDefault();
    }


    const cleanUserData = () => {
        setUserData({
            name: "",
            email: "",
            password: ""
        })
    };


    return (
        <main className="margin-bottom">
          <article>
            <h2>Register a profile!</h2>
            <form onSubmit={registerUser}>
              <div className="form-input-row">
                <label>Name</label>
                <input autoFocus type="text" required min="2" value={userData.name} name="name" placeholder="Vitalik Buterin" onChange={handleChange} />
              </div>
              <div className="form-input-row">
                <label>Email</label>
                <input type="email" name="email" value={userData.email} required placeholder="Il tuo indirizzo mail" onChange={handleChange} />
              </div>
              <div className="form-input-row password-check">
                <label>Password</label>
                <input id="pw" placeholder="Password (min. 8 caratteri)" name="password" pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}" required value={userData.password} onChange={handleChange} type="password" autoComplete="on" />
                <div className={`error-message ${pwStrong === false ? "" : "hidden"}`}>
                  <p>La password deve contenere min. 8 caratteri. Tra cui almeno un numero, una lettera piccola e una grande.</p>
                </div>
              </div>
              <span>
                <p>
                  Facendo clic su Registrati, accetti i nostri <a href="/terms" target="_blank">Termini</a>. Scopri come raccogliamo e archiviamo i tuoi dati su <a href="/privacy">Privacy Policy</a>.
                </p>
              </span>
              <br/>
              <button type="submit" className="button-standard button-form" disabled={`${!pwStrong ? "true" : ""}`}>
                Registrati
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
                  <path d="M8.122 24l-4.122-4 8-8-8-8 4.122-4 11.878 12z"></path>
                </svg>
              </button>
            </form>
          </article>
        </main>
    );
};

export default Register;
