import React, { useState, useEffect } from 'react';
//import Web3 from "web3";
import DVideo from "./../contracts/DVideo.json";
import getWeb3 from "./../web3/getWeb3";

const ipfsClient = require('ipfs-http-client')
const ipfs = ipfsClient({ host: 'ipfs.infura.io', port: 5001, protocol: 'https' })


const Video = () => {
    const [appState, setAppState] = useState({
        contractString: "",
        web3: {},
        accounts: {},
        contract: {}
    });

    const [message, setMessage] = useState("");

  // state = { storageValue: 0, web3: null, accounts: null, contract: null };
    useEffect(() => {
        initSetup();
    }, []);


    const initSetup = async () => {
        try {
          // Get network provider and web3 instance.
          const web3 = await getWeb3();
          // console.log("get web3: ", web3);

          // Use web3 to get the user's accounts.
          const accounts = await web3.eth.getAccounts();

          // Get the contract instance.
          const networkId = await web3.eth.net.getId();
          const deployedNetwork = await DVideo.networks[networkId];

          //load in the contract
          const instance = await new web3.eth.Contract(
            DVideo.abi,
            deployedNetwork && deployedNetwork.address //only if deployedNetwork exists
          );
          setAppState(prevState => {
              return { ...prevState, web3: web3 }
          });
          setAppState(prevState => {
              return { ...prevState, accounts: accounts }
          });
          setAppState(prevState => {
              return { ...prevState, contract: instance }
          });
        } catch (err) {
            // Catch any errors for any of the above operations.
            console.log("error man: ", err);
        }
    }


    const insertString = async (e) => {
        e.preventDefault();
        console.log(`inserting the message '${message}' into contract`);
        var res = await appState.contract.methods.set(message).send({from: appState.accounts[0]});
        console.log("res: ", res);
    }

    const readString = async () => {
        const response = await appState.contract.methods.myString().call();
        setAppState(prevState => {
            return { ...prevState, contractString: response }
        });
        alert(`Contract string is: ${response}`);
    }

    const setFile = async (e) => {
        e.preventDefault();
        const file = e.target.files[0];
        console.log("file: ", file);
        console.log("file size(MB): ", file.size / 1000000);
        const reader = new window.FileReader();
        console.log("reader: ", reader);
        await reader.readAsArrayBuffer(file);

        reader.onloadend = () => {
            setAppState(prevState => {
                return { ...prevState, buffer: Buffer(reader.result) }
            });
            console.log("buffer: ", appState.buffer);
        };
    }

    const uploadVideo = async (e) => {
        e.preventDefault();
        console.log("buffer is: ", appState.buffer);
        // console.log("ipfs: ", ipfs);
        ipfs.add(appState.buffer)
        .then(res => {
            console.log("ipfs RES: ", res);
        });
    }

//"QmNh4HXE5sPA1s9pGEnAkmBG4H4bdUbBwxhStnEA2AWswG"

    return(
      <>
      {!appState.web3 ? <div>Loading Web3, accounts, and contract...</div>
        :
        <div className="App">
          <h3>Upload video!</h3>
          <input type="file" accept=".mp4, .mov, .ogg, .wmv" onChange={setFile}/>
          <button onClick={uploadVideo}>Upload</button>
          {/* <button onClick={readString}>Read message</button> */}
          <div className="video-cont">
            <video playsInline disablePictureInPicture autoload="true" controls>
              <source src="https://ipfs.infura.io/ipfs/QmNh4HXE5sPA1s9pGEnAkmBG4H4bdUbBwxhStnEA2AWswG" type="video/mp4" />
            </video>
          </div>
        </div>
        }
      </>
    )
}

export default Video;
